import React, { Component } from 'react'
import ReactDOM from 'react-dom'

class TextDom extends Component {

    componentWillUnmount() {
        console.log('component did unmount')
    }
    render() {

        return (
            <div>TextDom</div>
        )
    }
}

class Clock extends Component {
    constructor() {
        super()
        this.state = {
            date: new Date()
        }
    }

    render() {
        return (
            <div>
                <h1>
                    <p>现在的时间是</p>
                    {this.state.date.toLocaleTimeString()}
                </h1>
            </div>
        )
    }
}

class Times extends Component {
    componentWillUnmount() {
        clearInterval(this.timer)
    }

    render() {
        return (
            <div>当前:{this.props.time || ''}</div>
        )
    }
}

class Stage2 extends Component {
    constructor() {
        super()
        this.state = {
            isShow: false,
            nowTime: new Date().getTime(),
        }
        console.log('construct')
    }

    componentWillMount() {
        this.timer = setInterval(() => {
            this.setState({ nowTime: new Date().getTime() })
        }, 1000);
        console.log('component will mount')
    }

    componentDidMount() {
        console.log('component did mount')
    }

    changeText() {
        this.setState({
            isShow: !this.state.isShow,
        })
    }

    render() {
        console.log('render')
        return (
            <div>
                {/* <Times time={this.state.nowTime} /> */}
                <div onClick={this.changeText.bind(this)}>显示/隐藏</div>
                {this.state.isShow ? <Times time={this.state.nowTime} /> : null}
            </div>
        )
    }
}

export default Stage2;
